import React from "react";
import { useState } from "react";

const Card = function () {
  const [position, setPosition] = useState({ top: "55%", left: "60%" });
  const [isEscaping, setIsEscaping] = useState(false);
  const [clicked, isClicked] = useState(false);

  function moveButton() {
    const randomTop = Math.floor(Math.random() * 60) + 20 + "%";
    const randomLeft = Math.floor(Math.random() * 60) + 20 + "%";

    setPosition({ top: randomTop, left: randomLeft });
  }
  return (
    <div className="container">
      <div className="card">
        {clicked ? <> I know my love you wont dare tell no</>:<>
          <h2>Will you be my Valentine? üíò</h2>
          <div className="buttonArea">
            <button className="btnYes" onClick={() => isClicked(true)}>
              Yes ‚ù§Ô∏è
            </button>
            <button
              className={`btnNo ${isEscaping ? "escape" : ""}`}
              style={isEscaping ? position : {}}
              onMouseEnter={() => {
                setIsEscaping(true);
                moveButton();
              }}
            >
              No üíî
            </button>
          </div>
        </>}
      </div>
    </div>
  );
};
export default Card;
